mapper.select:
SELECT heart
FROM member_meeting
WHERE id=#{memberId} AND meetingId=#{meetingId}

SELECT apply
FROM member_meeting
WHERE id=#{memberId} AND meetingId=#{meetingId}

SELECT MEETINGID
FROM MEMBER_MEETING
WHERE apply = 0 AND ID = #{memberId}

SELECT MEETINGID
FROM MEMBER_MEETING
WHERE heart = 1 AND ID = #{memberId}

SELECT MEETINGID
FROM MEMBER_MEETING
WHERE apply = 1 AND ID = #{memberId}
